import axios from 'axios';
// import { setAlert } from './alert';

import {
  GET_PROFILES,
  PROFILE_ERROR,
  CLEAR_PROFILE,
} from './types';

// Get all profiles
export const getProfiles = () => async dispatch => {
    dispatch({ type: CLEAR_PROFILE });

    try {
      const res = await axios.get('/api/profile');
      dispatch({
        type: GET_PROFILES,
        payload: res.data
      });
    } catch (err) {
      dispatch({
        type: PROFILE_ERROR,
        payload: { msg: err.statusText, status: err.status }
      });
    }
  };
